mapper.select:
select d.`id`, d.`title`, d.`content`, d.`link`, d.`material_cover_id`, (select m.pic_url from material m where d.material_cover_id=m.id) as coverPicUrl, d.`summary`, d.`material_id`, d.`wechat_id`, d.status, d.sequence, d.remark, d.material_category_id, (select m.`name` from material_category m where d.material_category_id=m.id) as categoryName, d.wx_image_text_id, d.tag_id, UNIX_TIMESTAMP(created_at) as created_at, d.laste_updated_by, UNIX_TIMESTAMP(laste_updated_at) as lasteUpdatedAt, d.send_times, d.read_times, d.qrcode_id, d.url from dcrm_image_text_detail d WHERE 1=1
AND (d.title like concat('%',#{search},'%')
or d.summary like concat('%',#{search},'%')
or d.remark like concat('%',#{search},'%'))
and d.`material_category_id` is null
and d.`material_category_id`=#{materialCategoryId}
order by d.${sidx} ${order}
order by d.laste_updated_at desc
limit #{offset}, #{limit}

mapper.update:
update dcrm_image_text_detail
qrcode_id=#{qrcodeId}, material_id=#{materialId}, send_times=#{sendTimes},
where id=#{id}

UPDATE dcrm_image_text_detail
title = #{title}, content = #{content}, link = #{link}, material_cover_id = #{materialCoverId}, summary = #{summary}, material_id = #{materialId}, wechat_id = #{wechatId}, status = #{status}, sequence = #{sequence}, remark = #{remark}, material_category_id = #{materialCategoryId}, wx_image_text_id = #{wxImageTextId}, tag_id = #{tagId}, created_at = #{createdAt}, created_by = #{createdBy}, laste_updated_at = #{lasteUpdatedAt}, laste_updated_by = #{lasteUpdatedBy}, send_times = #{sendTimes}, read_times = #{readTimes}, qrcode_id = #{qrcodeId}, url = #{url},
WHERE id = #{id}
