mapper.select:
select d.`id`, d.`title`, d.`content`, d.`link`, d.`material_cover_id`, (select m.pic_url from material m where d.material_cover_id=m.id) as coverPicUrl, d.`summary`, d.`material_id`, d.`wechat_id`, d.status, d.sequence, d.remark, d.material_category_id, (select m.`name` from material_category m where d.material_category_id=m.id) as categoryName, d.wx_image_text_id, d.tag_id, UNIX_TIMESTAMP(created_at) as created_at, d.laste_updated_by, UNIX_TIMESTAMP(laste_updated_at) as lasteUpdatedAt, d.send_times, d.read_times, d.qrcode_id from dcrm_image_text_detail d WHERE 1=1
AND (d.title like concat('%',#{search},'%')
or d.summary like concat('%',#{search},'%')
or d.remark like concat('%',#{search},'%'))
and d.`material_category_id`=#{materialCategoryId}
order by d.${sidx} ${order}
order by d.created_at desc
limit #{offset}, #{limit}

mapper.update:
update dcrm_image_text_detail
qrcode_id=#{qrcodeId}, material_id=#{materialId},
where id=#{id}
