mapper.select:
SELECT ic.id,ic.name,ic.method_type,ic.type,ic.event,ic.parameter,ic.description,ic.url,ic.sequence,ic.is_deleted,ic.created_at,ic.created_by,ic.updated_at,ic.updated_by,ib.name AS brand,ib.key,ib.secret
FROM interface_config ic
LEFT JOIN interface_brand ib ON ib.id = ic.brand
WHERE ic.is_deleted != 1
 AND ic.brand = #{brand} 
 AND ic.name = #{name} 
 AND ic.method_type = #{method_type} 
 AND ic.type = #{type} 
 AND ic.created_at  >=  #{create_from} 
 AND ic.create_at  <=  #{create_to} 
 AND ic.updated_at  >=  #{update_from} 
 AND ic.updated_at  <=  #{update_to}
