mapper.select:
		select a.id,
			   a.union_id,
			   a.open_id,
		       a.nickname,
		       a.sex,
			   a.country,
			   a.province,
		       a.city,
			   a.head_img_url,
		       a.created_at,
		       a.wechat_id,
		       a.mobile,
		       a.is_subscribe,
		       b.binding_over,
		       b.register_over,
		       b.birthday,
		       b.survey_over,
		       b.badges,
		       b.manual_country,
		       b.manual_province,
		       b.manual_city,
		       b.points_balance,
		       a.activity,
		       a.subscribe_at,
		       a.last_conversation_at
		  from member a
		left join member_benefit b
		   on a.open_id = b.open_id

   		a.wechat_id = #{wechatId} 

			and a.nickname like CONCAT('%',#{nickname},'%')


			and a.sex = #{sex}


			and a.country = #{country}


			and a.province = #{province}


			and a.city = #{city}



				and a.is_subscribe = 1


				and a.is_subscribe = 0



			and a.activity >= #{activityStartAt}


			and a.activity <= #{activityEndAt}


			and a.batchsend_month >= #{batchSendOfMonthStartAt}


			and a.batchsend_month <= #{batchSendOfMonthEndAt}


			and a.subscribe_at >= #{attentionStartAt}


			and a.subscribe_at <= #{attentionEndAt}


			and a.unsubscribe_at >= #{cancelSubscribeStartAt}


			and a.unsubscribe_at <= #{cancelSubscribeEndAt}


			and a.mobile like CONCAT('%',#{mobile},'%')




					and (a.is_subscribe = 1 and now()  <=  DATE_ADD(a.last_conversation_at, INTERVAL 48 DAY_HOUR))


					and (a.is_subscribe = 1 and a.last_conversation_at >= #{daytime})




					and (a.is_subscribe = 0 or a.last_conversation_at is null or (a.is_subscribe = 1 and now()  >  DATE_ADD(a.last_conversation_at, INTERVAL 48 DAY_HOUR)))


					and (a.is_subscribe = 0 or a.last_conversation_at is null or (a.is_subscribe = 1 and a.last_conversation_at < #{daytime}))







			order by a.${sortName}

				${sortDir}


				desc



			order by a.${sortName}

				${sortDir}


				desc




		order by a.id desc

	LIMIT ${offset},${pageSize}

		select count(a.id)
		  from member a
		left join member_benefit b
		   on a.open_id = b.open_id

   		a.wechat_id = #{wechatId} 

			and a.nickname like CONCAT('%',#{nickname},'%')


			and a.sex = #{sex}


			and a.country = #{country}


			and a.province = #{province}


			and a.city = #{city}



				and a.is_subscribe = 1


				and a.is_subscribe = 0



			and a.activity >= #{activityStartAt}


			and a.activity <= #{activityEndAt}


			and a.batchsend_month >= #{batchSendOfMonthStartAt}


			and a.batchsend_month <= #{batchSendOfMonthEndAt}


			and a.subscribe_at >= #{attentionStartAt}


			and a.subscribe_at <= #{attentionEndAt}


			and a.unsubscribe_at >= #{cancelSubscribeStartAt}


			and a.unsubscribe_at <= #{cancelSubscribeEndAt}


			and a.mobile like CONCAT('%',#{mobile},'%')




					and (a.is_subscribe = 1 and now()  <=  DATE_ADD(a.last_conversation_at, INTERVAL 48 DAY_HOUR))


					and (a.is_subscribe = 1 and a.last_conversation_at >= #{daytime})




					and (a.is_subscribe = 0 or a.last_conversation_at is null or (a.is_subscribe = 1 and now()  >  DATE_ADD(a.last_conversation_at, INTERVAL 48 DAY_HOUR)))


					and (a.is_subscribe = 0 or a.last_conversation_at is null or (a.is_subscribe = 1 and a.last_conversation_at < #{daytime}))
